import React from 'react';
import ReactApexChart from 'react-apexcharts';
import axios from 'axios';

      class Pie extends React.Component {
        constructor(props) {
          super(props);

          this.state = {
          
            series: [null, null, null],
            options: {
              chart: {
                width: 380,
                type: 'pie',
              },
              labels: ['Solved', 'Attempted', 'Partially'],
              responsive: [{
                breakpoint: 480,
                options: {
                  chart: {
                    width: 200
                  },
                  legend: {
                    position: 'bottom'
                  }
                }
              }]
            },
          
          
          };
        }

        componentDidMount(){
          this.new();
        }

        new(){
          axios.post('http://54.237.113.3/', {
            "mail" : "mohit@gmail.com",
            "type" : "getdata",
            // "firstName" : "Mohit",
            // "lastName" : "Bisht",
            // "userName" : "Mohitb07",
            // "passWord" : "server"    
          }).then(response => {
            console.table(response.data.userDetail)
            const newSeries = Object.values(response.data.userDetail)
            console.log(newSeries[2])
            console.log(newSeries[3])
            console.log(newSeries[4])

          }).catch(error => {
            console.log(error)
          })
        }

        render() {
          return (        
          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="pie" width={380} />
          </div>
          );
        }
      }

export default Pie;
