import React from 'react';
import ReactApexChart from 'react-apexcharts';
import axios from 'axios';

      class Pie extends React.Component {
        constructor(props) {
          super(props);

          this.state = {
          
            series: [44, 55, 13],
            options: {
              chart: {
                width: 380,
                type: 'pie',
              },
              labels: ['Solved', 'Unsolved', 'Partially'],
              responsive: [{
                breakpoint: 480,
                options: {
                  chart: {
                    width: 200
                  },
                  legend: {
                    position: 'bottom'
                  }
                }
              }]
            },
          
          
          };
        }

        componentDidMount(){
          axios.get('http://54.237.113.3/',{
            "mail" : "akshat@gmail.com",
            "type" : "createuser",
            "firstName" : "akshat",
            "lastName" : "jindal",
            "userName" : "akshat123",
            "passWord" : "server"
          }).then(res=>{
            console.log('respose' + res)
          }).catch(error => {
            console.log('error ' + error)
          })
        }
      

        render() {
          return (        
          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.state.series} type="pie" width={380} />
          </div>
          );
        }
      }

export default Pie;
