import React from 'react';
import ReactApexChart from 'react-apexcharts';

      class Pie extends React.Component {
        constructor(props) {
          super(props);

          this.state = {
          
            series: this.props.series,
            options: {
              chart: {
                width: 380,
                type: 'pie',
              },
              labels: ['Solved', 'Partially', 'Attempted'],
              responsive: [{
                breakpoint: 480,
                options: {
                  chart: {
                    width: 200
                  },
                  legend: {
                    position: 'bottom'
                  }
                }
              }]
            },
          
          
          };
        }
        render() {
          return (        
          <div id="chart">
            <ReactApexChart options={this.state.options} series={this.props.series} type="pie" width={380} />
          </div>
          );
        }
      }

export default Pie;
